-- Растения (агро) + пороги/нормы
create table if not exists plants (
  id bigserial primary key,
  name text not null,
  category text not null default 'agro',  -- можно пригодится дальше

  -- Порог замерзания / засыхания (по температуре воздуха)
  freeze_temp_c numeric(5,2) not null,   -- ниже этого растению плохо/мерзнет
  dry_temp_c    numeric(5,2) not null,   -- выше этого сохнет/стресс

  -- Нормы (диапазоны)
  air_temp_min_c numeric(5,2),
  air_temp_max_c numeric(5,2),

  air_humidity_min_pct numeric(5,2),
  air_humidity_max_pct numeric(5,2),

  water_temp_min_c numeric(5,2),
  water_temp_max_c numeric(5,2),

  -- Soil как “сырое значение датчика” (у тебя 43786)
  soil_min_raw bigint,
  soil_max_raw bigint,

  light_min_lux numeric(10,2),
  light_max_lux numeric(10,2),

  -- Давление (hPa) и загрязнение (условно ppm или AQI — выбери что у тебя)
  pressure_min_hpa numeric(7,2),
  pressure_max_hpa numeric(7,2),

  pollution_max numeric(10,2), -- максимум допустимого загрязнения (ppm/AQI/мкг/м3)

  created_at timestamptz not null default now()
);

-- Измерения датчиков (история)
create table if not exists measurements (
  id bigserial primary key,
  plant_id bigint not null references plants(id) on delete cascade,

  air_temp_c numeric(5,2),
  air_humidity_pct numeric(6,3),

  water_temp_c numeric(5,2),

  soil_raw bigint,

  light_lux numeric(12,4),

  pollution numeric(12,4),
  pressure_hpa numeric(8,2),

  measured_at timestamptz not null default now()
);

